import React, { useEffect, useState } from 'react';
import ShipList from '../components/ships/ShipList';
import ShipForm from '../components/ships/ShipForm';



const ShipsPage = () => {
    const [ships, setShips] = useState([]);
    const [editingShip, setEditingShip] = useState(null);

    useEffect(() => {
        const data = JSON.parse(localStorage.getItem('ships')) || [];
        setShips(data);
    }, []);

    const saveShips = (updatedShips) => {
        localStorage.setItem('ships', JSON.stringify(updatedShips));
        setShips(updatedShips);
    };

    const handleSave = (ship) => {
        let updatedShips;
        if (ship.id) {
            updatedShips = ships.map((s) => (s.id === ship.id ? ship : s));
        } else {
            ship.id = Date.now().toString();
            updatedShips = [...ships, ship];
        }
        saveShips(updatedShips);
        setEditingShip(null);
    };

    const handleDelete = (id) => {
        const updated = ships.filter((ship) => ship.id !== id);
        saveShips(updated);
    };

    const handleEdit = (ship) => {
        setEditingShip(ship);
    };

    return (
        <div style={{ padding: '2rem' }}>
            <ShipForm onSave={handleSave} editingShip={editingShip} />
            <hr />
            <ShipList ships={ships} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default ShipsPage;
