import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Charts from '../components/Dashboard/Charts';

const DashboardPage = () => {
  const [ships, setShips] = useState([]);
  const [components, setComponents] = useState([]);
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const shipData = JSON.parse(localStorage.getItem('ships')) || [];
    const compData = JSON.parse(localStorage.getItem('components')) || [];
    const jobData = JSON.parse(localStorage.getItem('jobs')) || [];

    setShips(shipData);
    setComponents(compData);
    setJobs(jobData);
  }, []);

  const overdueComponents = components.filter(c => new Date(c.lastMaintenanceDate) < new Date());
  const jobsInProgress = jobs.filter(j => j.status === 'Open' || j.status === 'In Progress');
  const jobsCompleted = jobs.filter(j => j.status === 'Completed');

  return (
    <div>
      <Navbar />
      <div style={{ padding: '2rem' }}>
        <h1>Dashboard</h1>
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '1.5rem',
            marginTop: '2rem'
          }}
        >
          <div style={cardStyle}>
            <h3>Total Ships</h3>
            <p>{ships.length}</p>
          </div>
          <div style={cardStyle}>
            <h3>Overdue Components</h3>
            <p>{overdueComponents.length}</p>
          </div>
          <div style={cardStyle}>
            <h3>Jobs In Progress</h3>
            <p>{jobsInProgress.length}</p>
          </div>
          <div style={cardStyle}>
            <h3>Jobs Completed</h3>
            <p>{jobsCompleted.length}</p>
          </div>
        </div>
        <Charts />
      </div>
    </div>
  );
};

const cardStyle = {
  background: '#f9f9f9',
  border: '1px solid #ddd',
  borderRadius: '10px',
  padding: '1rem',
  boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
  textAlign: 'center'
};

export default DashboardPage;
