import React, { useEffect, useState } from 'react';
import Charts from '../components/Dashboard/Charts';
import '../styles/main.css';

const DashboardPage = () => {
  const [ships, setShips] = useState([]);
  const [components, setComponents] = useState([]);
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const appData = JSON.parse(localStorage.getItem('appData')) || {};
    setShips(appData.ships || []);
    setComponents(appData.components || []);

    // Get jobs and update status if needed (example: convert 'Open' -> 'In Progress')
    let jobsData = appData.jobs || [];

    // Example update: Change 'Open' jobs to 'In Progress' (you can change this logic)
    jobsData = jobsData.map(job => {
      if (job.status === 'Open') {
        return { ...job, status: 'In Progress' };
      }
      return job;
    });

    setJobs(jobsData);
  }, []);

  const overdueComponents = components.filter(
    (c) => new Date(c.lastMaintenanceDate) < new Date()
  );

  // Adjust jobsInProgress to match updated statuses
  const jobsInProgress = jobs.filter(
    (j) => j.status === 'Open' || j.status === 'In Progress'
  );

  const jobsCompleted = jobs.filter((j) => j.status === 'Completed');

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <div className="dashboard-grid">
        <div className="kpi-card">
          <h3>Total Ships</h3>
          <p>{ships.length}</p>
        </div>
        <div className="kpi-card">
          <h3>Overdue Components</h3>
          <p>{overdueComponents.length}</p>
        </div>
        <div className="kpi-card">
          <h3>Jobs In Progress</h3>
          <p>{jobsInProgress.length}</p>
        </div>
        <div className="kpi-card">
          <h3>Jobs Completed</h3>
          <p>{jobsCompleted.length}</p>
        </div>
      </div>
      <Charts jobs={jobs} />
    </div>
  );
};

export default DashboardPage;
