import React from 'react';

const JobList = ({ jobs, onEdit, onDelete, onView = () => { } }) => {
    return (
        <div>
            <h2>Maintenance Jobs</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th style={thStyle}>Type</th>
                        <th style={thStyle}>Ship ID</th>
                        <th style={thStyle}>Component ID</th>
                        <th style={thStyle}>Priority</th>
                        <th style={thStyle}>Status</th>
                        <th style={thStyle}>Scheduled Date</th>
                        <th style={thStyle}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {jobs.length === 0 && (
                        <tr>
                            <td colSpan="7" style={{ textAlign: 'center', padding: '1rem' }}>
                                No jobs available.
                            </td>
                        </tr>
                    )}
                    {jobs.map((job) => (
                        <tr key={job.id} style={{ borderBottom: '1px solid #ddd' }}>
                            <td style={tdStyle}>{job.type}</td>
                            <td style={tdStyle}>{job.shipId}</td>
                            <td style={tdStyle}>{job.componentId}</td>
                            <td style={tdStyle}>{job.priority}</td>
                            <td style={tdStyle}>{job.status}</td>
                            <td style={tdStyle}>{job.scheduledDate}</td>
                            <td style={tdStyle}>
                                <button onClick={() => onView(job)} style={buttonStyle}>View</button>{' '}
                                <button onClick={() => onEdit(job)} style={buttonStyle}>Edit</button>{' '}
                                <button onClick={() => onDelete(job.id)} style={buttonStyle}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const thStyle = {
    borderBottom: '2px solid #ccc',
    padding: '0.5rem',
    textAlign: 'left',
};

const tdStyle = {
    padding: '0.5rem',
};

const buttonStyle = {
    marginRight: '0.5rem',
    padding: '0.25rem 0.5rem',
    cursor: 'pointer',
};

export default JobList;
