import React from 'react';

const ShipList = ({ ships, onEdit, onDelete, onView }) => {
    // Safeguard: define fallback if onView not passed
    const safeOnView = onView || (() => { });

    return (
        <div>
            <h2>All Ships</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th style={thStyle}>Name</th>
                        <th style={thStyle}>IMO</th>
                        <th style={thStyle}>Flag</th>
                        <th style={thStyle}>Status</th>
                        <th style={thStyle}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {ships.map((ship) => (
                        <tr key={ship.id} style={{ borderBottom: '1px solid #ddd' }}>
                            <td style={tdStyle}>{ship.name}</td>
                            <td style={tdStyle}>{ship.imo}</td>
                            <td style={tdStyle}>{ship.flag}</td>
                            <td style={tdStyle}>{ship.status}</td>
                            <td style={tdStyle}>
                                <button onClick={() => safeOnView(ship)} style={buttonStyle}>View</button>{' '}
                                <button onClick={() => onEdit(ship)} style={buttonStyle}>Edit</button>{' '}
                                <button onClick={() => onDelete(ship.id)} style={buttonStyle}>Delete</button>
                            </td>
                        </tr>
                    ))}
                    {ships.length === 0 && (
                        <tr>
                            <td colSpan="5" style={{ textAlign: 'center', padding: '1rem' }}>
                                No ships available.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

const thStyle = {
    borderBottom: '2px solid #ccc',
    padding: '0.5rem',
    textAlign: 'left',
};

const tdStyle = {
    padding: '0.5rem',
};

const buttonStyle = {
    marginRight: '0.5rem',
    padding: '0.25rem 0.5rem',
    cursor: 'pointer',
};

export default ShipList;
