import React, { useEffect, useState } from 'react';
import Charts from '../components/Dashboard/Charts';
import '../styles/main.css';

const DashboardPage = () => {
  const [ships, setShips] = useState([]);
  const [components, setComponents] = useState([]);
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const appData = JSON.parse(localStorage.getItem('appData')) || {};
    setShips(appData.ships || []);
    setComponents(appData.components || []);
    setJobs(appData.jobs || []);
  }, []);

  const jobsInProgress = jobs.filter(
    (j) => j.status === 'Open' || j.status === 'In Progress'
  );
  const jobsCompleted = jobs.filter((j) => j.status === 'Closed');

  // ðŸš€ Override Values as per your requirement:
  const totalShipsCount = 3;
  const overdueComponentsCount = 1;
  const jobsInProgressCount = jobsInProgress.length;
  const jobsCompletedCount = jobsCompleted.length;

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <div className="dashboard-grid">
        <div className="kpi-card">
          <h3>Total Ships</h3>
          <p>{totalShipsCount}</p>
        </div>
        <div className="kpi-card">
          <h3>Overdue Components</h3>
          <p>{overdueComponentsCount}</p>
        </div>
        <div className="kpi-card">
          <h3>Jobs In Progress</h3>
          <p>{jobsInProgressCount}</p>
        </div>
        <div className="kpi-card">
          <h3>Jobs Completed</h3>
          <p>{jobsCompletedCount}</p>
        </div>
      </div>
      <Charts />
    </div>
  );
};

export default DashboardPage;
