import React, { useEffect, useState } from 'react';
import JobList from '../components/jobs/JobList';
import JobForm from '../components/jobs/JobForm';

const JobsPage = () => {
    const [jobs, setJobs] = useState([]);
    const [editingJob, setEditingJob] = useState(null);
    const [viewingJob, setViewingJob] = useState(null);

    useEffect(() => {
        const data = JSON.parse(localStorage.getItem('jobs')) || [];
        setJobs(data);
    }, []);

    const saveJobs = (updatedJobs) => {
        localStorage.setItem('jobs', JSON.stringify(updatedJobs));
        setJobs(updatedJobs);
    };

    const handleSave = (job) => {
        let updatedJobs;
        if (job.id) {
            updatedJobs = jobs.map((j) => (j.id === job.id ? job : j));
        } else {
            job.id = Date.now().toString();
            updatedJobs = [...jobs, job];
        }
        saveJobs(updatedJobs);
        setEditingJob(null);
    };

    const handleDelete = (id) => {
        const updated = jobs.filter((j) => j.id !== id);
        saveJobs(updated);
    };

    const handleEdit = (job) => {
        setEditingJob(job);
    };

    const handleView = (job) => {
        setViewingJob(job);
        alert(`Viewing Job:\nType: ${job.type}\nStatus: ${job.status}\nPriority: ${job.priority}`);
    };

    return (
        <div style={{ padding: '2rem' }}>
            <JobForm onSave={handleSave} editingJob={editingJob} />
            <hr />
            <JobList
                jobs={jobs}
                onEdit={handleEdit}
                onDelete={handleDelete}
                onView={handleView} // ✅ passed here
            />
        </div>
    );
};

export default JobsPage;
