import React, { useEffect, useState } from 'react';

const JobCalendar = () => {
    const [jobs, setJobs] = useState([]);
    const [selectedDate, setSelectedDate] = useState('');
    const [jobsForDate, setJobsForDate] = useState([]);

    useEffect(() => {
        const data = JSON.parse(localStorage.getItem('jobs')) || [];
        setJobs(data);
    }, []);

    const handleDateChange = (e) => {
        const date = e.target.value;
        setSelectedDate(date);
        const filtered = jobs.filter(job => job.scheduledDate === date);
        setJobsForDate(filtered);
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Maintenance Job Calendar</h2>
            <input
                type="date"
                value={selectedDate}
                onChange={handleDateChange}
                style={styles.dateInput}
            />

            {selectedDate && (
                <div style={styles.jobListContainer}>
                    <h3 style={styles.subTitle}>Jobs scheduled for {selectedDate}</h3>
                    {jobsForDate.length === 0 ? (
                        <p style={styles.noJobsText}>No jobs scheduled for this date.</p>
                    ) : (
                        <ul style={styles.jobList}>
                            {jobsForDate.map((job) => (
                                <li key={job.id} style={styles.jobCard}>
                                    <strong>{job.type}</strong>
                                    <p>Ship ID: {job.shipId}</p>
                                    <p>Component ID: {job.componentId}</p>
                                    <p>Priority: {job.priority}</p>
                                    <p>Status: {job.status}</p>
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        maxWidth: 400,
        margin: '1rem auto',
        padding: 20,
        borderRadius: 8,
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
        backgroundColor: '#fff',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    },
    title: {
        textAlign: 'center',
        marginBottom: 20,
        color: '#333',
    },
    dateInput: {
        width: '100%',
        padding: '10px 14px',
        fontSize: 16,
        borderRadius: 6,
        border: '1px solid #ccc',
        marginBottom: 20,
        boxSizing: 'border-box',
    },
    jobListContainer: {
        marginTop: 10,
    },
    subTitle: {
        fontSize: 18,
        marginBottom: 10,
        color: '#444',
    },
    noJobsText: {
        textAlign: 'center',
        color: '#888',
        fontStyle: 'italic',
    },
    jobList: {
        listStyleType: 'none',
        padding: 0,
        margin: 0,
        display: 'flex',
        flexDirection: 'column',
        gap: 12,
    },
    jobCard: {
        backgroundColor: '#f9f9f9',
        borderRadius: 8,
        padding: 12,
        boxShadow: '0 1px 4px rgba(0,0,0,0.05)',
        color: '#333',
        fontSize: 14,
    },
};

export default JobCalendar;
